    for (unsigned int step = 0; step < nSteps; step++) {

        std::cout << "\x1B[2J\x1B[HBoard state:" << std::endl;
        for (unsigned int i = 0; i < board_state.size() / rowLength; i++) {
            for (unsigned int j = 0; j < rowLength; j++) {
                std::cout << (board_state[i*rowLength+j] == 1. ? "■ " : "· ");
            }
            std::cout << std::endl;
        }
        
        // encrypt the board state 
        //std::cout << "\nEncrypt the board state" << std::endl;
        Plaintext p = cc->MakeCKKSPackedPlaintext(board_state);
        auto enc_board = cc->Encrypt(keys.publicKey, p);
        //std::cout << "Board state encrypted" << std::endl;

        // update the state
        //std::cout << "Update the board state" << std::endl;
        auto enc_board_updated = update(enc_board, rowLength, cc);
        //std::cout << "Board state updated" << std::endl;
        
        // reduce the noise (requires a mult depth of 9)
        // enc_board_updated = reduce_noise(enc_board_updated, cc);

        // decrypt the result
        //std::cout << "Decrypt the result" << std::endl;
        Plaintext result;
        cc->Decrypt(keys.secretKey, enc_board_updated, &result);
        unsigned int i = 0;
        for (auto x: (*result).GetRealPackedValue()) {
            // if (i == 0) { std::cout << x << std::endl; }
            board_state[i] = (int) (x + 0.5);
            ++i;
        }
        //std::cout << "Result decrypted" << std::endl;
    }

    std::cout << "\nFinal state:" << std::endl;
    for (unsigned int i = 0; i < board_state.size() / rowLength; i++) {
        for (unsigned int j = 0; j < rowLength; j++) {
                std::cout << (board_state[i*rowLength+j] == 1. ? "■ " : "· ");
        }
        std::cout << std::endl;
    }
    
    return 0;
